void UUI_GameInstance::Init()
{
	// Call base implementation
	Super::Init();

	// Call the blueprint event
	Init_BP();
}

void UUI_GameInstance::Shutdown()
{
	// Call the blueprint event
	Shutdown_BP();

	// Call base implementation
	Super::Shutdown();
}

FName UUI_GameInstance::MakeInputIconKey(const FText& KeyDisplayName) const
{
    FString KeyString = KeyDisplayName.ToString();

    // Remove whitespace
    KeyString.TrimStartAndEndInline();

    // Make key string all uppercase to prevent case sensitivity issues
    KeyString = KeyString.ToUpper();

    return FName(*KeyString);
}

UTexture2D* UUI_GameInstance::GetInputIconTexture(const FText& KeyDisplayName) const
{
    const FName Key = MakeInputIconKey(KeyDisplayName);

    const TSoftObjectPtr<UTexture2D>* IconTexture = InputIconMap.Find(Key);
    if (!IconTexture)
    {
        UE_LOG(LogTemp, Warning, TEXT("No input icon found for key '%s'"), *Key.ToString());
        return nullptr;
    }

    // Load the texture synchronously if not already loaded
	// Change to async or adjust how it loads if performance issues arise
    return IconTexture->LoadSynchronous();
}